example.code:
function showValues() {
  var str = $("form").serialize();
  $("#results").text( str );
}
$("input[type='checkbox'], input[type='radio']").on( "click", showValues );
$("select").on( "change", showValues );
showValues();

example.css:
body, select { font-size:12px; }
form { margin:5px; }
p { color:red; margin:5px; font-size:14px; }
b { color:blue; }
